INSERT INTO `oa_recruit` VALUES (7, '贾梦茹', 2015210944, '女', '18756011348', '958691150', 5, 1494983406, 1, 1, 1, '材料科学与工程学院', '金属材料工程', '大一就读材料院金属材料专业并任团支书职务，在戴尔课戴表项目组实习一年并获得实习证明，大二转入管理学院信息系统与信息管理专业。性格上进，勤奋，有耐心，略逗。');
INSERT INTO `oa_recruit` VALUES (8, '刘倩', 2015213511, '女', '17730226331', '635768851', 9, 1494986909, 1, 3, 1, '管理学院', '信息管理与信息系统', '我是管理学院会计学15-2班的刘倩，担任管理学院辅导员助理，大一担任团支书时曾运营过班级qq空间网络平台，平时喜欢刷朋友圈和微博的我，也想学一些新媒体建设的技能^_^');
INSERT INTO `oa_recruit` VALUES (9, '常娅菲', 2015212001, '女', '18755176036', '651319490', 4, 1494991652, 1, 4, 2, '化学与化工学院', '高分子材料与工程', '本人熟悉Ps、Ae软件操作，爱好绘画、摄影。独立完成过多条宣传视频的拍摄和制作、设计制作过回访母校的小本宣传册，完成过多张海报制作…有承办多项校级活动的工作经历。\n  本人为大二化院学生，存在今年学期末搬校区问题，不知贵工作室对此是否有什么要求。谢谢^ - ^');
