INSERT INTO `oa_recruit` VALUES (17, '杨坤德', 2016212450, '男', '18959605628', '958500892', 1, 1496801759, 1, 4, 2, '化学与化工学院', '化学工程与工艺', '我是个热情外向，工作积极的人。在班级中担任班长，负责班级大小事务的安排，能较好的与他人进行沟通协商。在大学生活中，我还担任团总支团务中心的干事，也有导助经历。凭着自己认真负责，不拖拉不应付的态度，相信我能够符合申请要求，在这个岗位上贡献自己的一份力。');
INSERT INTO `oa_recruit` VALUES (18, '甄敏', 2015213731, '女', '18856966979', '1326733528', 7, 1496801778, 1, 1, 1, '管理学院', '电子商务', '一个爱写东西的假文艺一枚。在过去的两年大学时光里，我接触了一些有关微信公众号文稿的工作和伙伴。来这里是想学习视频制作这些我感兴趣的东西，认识更多有趣的人。');
INSERT INTO `oa_recruit` VALUES (19, '蔡汝艳', 2015213519, '女', '18756502808', '1258293979', 1, 1496835913, 1, 2, 1, '管理学院', '信息管理与信息系统', '我是管理学院信息管理与信息系统15-2班的一员，入大学已经快2年了，体验了好多，也还有好多想去体验，一直都在争取补充自己的机会，想突破自己，发挥潜能。我觉得自己挺认真负责，不喜欢把事情拖到最后，尽力把事情做到不使自己后悔。');
INSERT INTO `oa_recruit` VALUES (20, '蔡汝艳', 2015213519, '女', '18756502808', '1258293979', 1, 1496835916, 1, 3, 1, '管理学院', '信息管理与信息系统', '我是管理学院信息管理与信息系统15-2班的一员，入大学已经快2年了，体验了好多，也还有好多想去体验，一直都在争取补充自己的机会，想突破自己，发挥潜能。我觉得自己挺认真负责，不喜欢把事情拖到最后，尽力把事情做到不使自己后悔。');
INSERT INTO `oa_recruit` VALUES (21, '曹善敏', 20132101, '男', '15755151515', '1212', 1, 1496919336, 2, 0, 1, '未知', '未知', '测试');
INSERT INTO `oa_recruit` VALUES (22, '胡成', 2016170041, '男', '18656550443', '1203662340', 3, 1496927127, 1, 4, 1, '未知', '未知', '我是仪器学院的研一学生，主要是从事Java、Android方面的开发，以Android为主（已经有两个项目快完成了），同时也会一些PHP。我想借此机会能够和更多爱好编程的人一起探讨、研究技术。空闲的时候喜欢打打羽毛球，看一些名著，也会经常去图书馆借编程方面的书看。');
INSERT INTO `oa_recruit` VALUES (23, '潘树飞', 2016110019, '男', '13156527616', '1114689081', 3, 1496927689, 1, 1, 1, '未知', '未知', '本人现读仪器学院研究生一年级，本科就读于山东科技大学，本科期间学习的是硬件电路方面的知识，研一期间，由于自己对互联网行业比较感兴趣，喜欢编程序，再加上项目需求，一直在学习PHP和MySQL，并开发了防伪防窜货的管理后台，现在功能基本已经实现。由于不是科班出身，基础比较薄弱，需要学习的东西很多，真心希望可以进入团队，一起学习，一起进步。');
INSERT INTO `oa_recruit` VALUES (24, '叶寿洪', 2016211779, '男', '15156526323', '1527864005', 7, 1497027687, 1, 1, 2, '电气与自动化工程学院', '智能电网信息工程', '之前是一名校团委新媒体应用研究中心综合事务部干事，主要参与举办中心素拓、人资培训等活动，参与文件起草、统筹协调、机制建设、资产管理、数据统计以及“小葫芦”微信号的运营工作。在中心里主要培养了组织策划等能力，同时我对新媒体内容也产生了极大的兴趣，特别是最容易让人接受的视频，同时也是最具有传播力的一种方式，所以希望加入大学生网络发展中心服务同学、锻炼自己。');
INSERT INTO `oa_recruit` VALUES (25, '陈丹红', 2015213630, '女', '15968248855', '827472080', 4, 1497575694, 1, 4, 1, '管理学院', '工商管理', '踏实好学，对新事物感兴趣。');
INSERT INTO `oa_recruit` VALUES (26, '孙宇博', 2015213669, '女', '18604380667', '776728853', 4, 1497575803, 1, 4, 1, '管理学院', '电子商务', '我是电子商务15级的学生，对于设计方面很感兴趣，只会特别特别特别简单的ps，希望能力方面能有所长进');
INSERT INTO `oa_recruit` VALUES (27, '陈丹红', 2015213630, '女', '15968248855', '827472080', 4, 1497583777, 1, 0, 1, '管理学院', '工商管理', '踏实好学。');
INSERT INTO `oa_recruit` VALUES (28, '杨佳宇', 2015211848, '男', '18705691916', '5246630', 5, 1497701167, 2, 0, 2, '计算机与信息学院', '电子信息工程', '参加了今晚贵工作室联合举办的Online Dev开发大会，认识到了自身能力方面的狭隘，想通过和dalao们的交流学习提高动手能力。我是计算机学院15级本科生，任学院辅导员助理。熟练使用PR、AE，在我院实验室参与过基于mfc的win软件开发，在其他方面基本没有开发经验。对产品和设计非常感兴趣，未来有志于从事产品方面的工作。个人缺乏相关经验，但有热情，做事细致有责任心。课余喜欢读一些互联网方面的书籍，对各大互联网公司的成长历程及成功因素有一定了解。希望能加入明理苑这个大家庭，和我校的有识之士共同成长进步。');
