INSERT INTO `oa_recruit` VALUES (35, '常立', 2017213055, '男', '18014529203', '1334084044', 8, 1502982895, 1, 0, 1, '未知', '未知', '我觉得一个好的成员首先要的就是热情，一直喜欢一种植物叫做向日葵，虽然没什么经验，但努力向光生长，这就是我，希望加入你们');
INSERT INTO `oa_recruit` VALUES (36, '姜晗', 2016212150, '女', '15209828286', '2316801398', 3, 1504110574, 1, 2, 2, '计算机与信息学院', '电子信息工程', '我是计算机学与信息院电子信息工程专业的一名学生，对网页制作，前端开发很感兴趣，也学过C++,html5,了解一些CSS，正在学习java，想进入技术部遇见更多志同道合的朋友，互相学习，取长补短，也想将学到的东西运用到实际中。');
INSERT INTO `oa_recruit` VALUES (37, '刘静', 2016213510, '女', '18226188949', '812082142', 2, 1504250019, 2, 0, 2, '建筑与艺术学院', '环境设计', '来自建筑与艺术学院艺术设计专业的一名学生，性格随和，擅长美术，喜欢音乐，做事认真严谨，喜欢有计划的完成任务，对待他人温柔随和，能很好的处理好人际关系。');
INSERT INTO `oa_recruit` VALUES (38, '刘子瑜', 2016215997, '女', '18326100819', '2476202123', 3, 1504355624, 1, 4, 2, '经济学院', '经济与贸易类（中外合作办学）', '已学完javaSE,正在学javaEE.\n因为喜欢，所以想向前辈学习');
INSERT INTO `oa_recruit` VALUES (39, '崔天昊', 2017210663, '男', '18609652537', '807201150', 3, 1504356791, 2, 1, 2, '未知', '未知', '我是来自北方的汉子，在技术方面我算个小白吧。但我会虚心学习，我很谦虚。我渴望能在大学锻炼自己，多多的掌握各种技术。当然我也会进全力为明理苑做出贡献的。我比较内向，但我一路走来也在不断的磨练自己。我当时报考时想学习计算机专业，但由于分数的问题报不上。技多不压身。');
INSERT INTO `oa_recruit` VALUES (40, '孙博瀚', 2016215519, '男', '18837450176', '2088704848', 3, 1504356919, 2, 2, 2, '汽车与交通工程学院', '车辆工程', '16级车辆工程  孙博瀚\n个人能力:自学Python，用Python做过网络爬虫和简单的数据分析，正在自学用Python实现机器学习\n能熟练使用PS,AE,PR,AU(Adobe Audition)完成海报和视频的制作，之前在汽交的技术运营部工作过(部门职责:做海报做视频)\n自学过51单片机和altim designer，可以独立画出焊接PCB板，准备自学树莓派');
INSERT INTO `oa_recruit` VALUES (41, '钱柔柔', 2017214106, '男', '17356585336', '1823775516', 8, 1504357396, 1, 0, 1, '未知', '未知', '活泼开朗，爱学习，好奇心强。');
INSERT INTO `oa_recruit` VALUES (42, '钱柔柔', 2017214106, '男', '17356585336', '1823775516', 7, 1504357662, 1, 0, 1, '未知', '未知', '活泼开朗，好奇心强，爱学习');
INSERT INTO `oa_recruit` VALUES (43, '刘佳伟', 2016211489, '男', '17356585172', '1287505846', 2, 1504362516, 1, 1, 1, '电气与自动化工程学院', '电气工程及其自动化', '经过一年的大学生活来到大二更加的明白了自己想要的发展和想做的事情。机缘巧合下了解到明理苑这个优秀的平台，对于个人来说首先我热衷于结交更多的朋友，开朗外向的性格，有着较强的逻辑能力，了解线上线下的推广方式有敢于创新的精神，能熟练使用微信公众号，微博，百度贴吧，QQ等网络推广方式。所以在认真考虑下希望加入明理苑推广部，相信自己在逐渐的学习下能够胜任在推广部的工作。在这个优秀的平台下能够进一步的提升自己，结识更多志同道合的朋友，认识更多优秀的同学');
INSERT INTO `oa_recruit` VALUES (44, '余文颖', 2014213857, '女', '15005516312', '735896022', 3, 1504434090, 2, 1, 1, '生物学院', '食品14-1班', '首先，作为信管14级即将下架的一名老学姐，我的内心是伤感的，更令人遗憾的是，由于对课堂上的理论缺乏实际的应用，当我面对各种编程语言时我只是知道“他们”是什么，却不知道如何正确地使用“他们”做出我想要的东西，这实在是太郁闷啦！我觉得编程是一件很神奇的事情，而且我也很有兴趣学习，所以想要有学习一些实际技术的机会，没想到这个机会就出现了！没错那就是明理苑大学生网络文化工作室的技术部！\n      尽管我可能变不成一个非常厉害的程序猿，但是说不定我能够认识很多程序猿，这也是挺不错的......总之，以我前几年还凑合的学习成绩保证，虽然我有很多不足的地方，但是我会努力哒！嗯！');
INSERT INTO `oa_recruit` VALUES (45, '张诗晨', 2015213467, '女', '15256951639', '1780295330', 1, 1504435258, 2, 0, 1, '管理学院', '信息管理与信息系统', '哈哈哈');
INSERT INTO `oa_recruit` VALUES (46, '贾嫣然', 2014213732, '女', '18455156309', '495973047', 6, 1504435744, 1, 4, 2, '马克思学院', '思政14-1班', '我叫贾嫣然，今年21岁。我的性格比较活泼开朗，我为人也很热情。在平时的生活中，我喜欢读书，最喜欢的是玛格丽特·杜拉斯的《情人》。也喜欢追剧，当然并没有时时刻刻都抱着电视。放假回家的时候，我也会做一些小甜品。我一直坚信，生活是美好的，所以很多没经历过的事都值得去期待。');
INSERT INTO `oa_recruit` VALUES (47, '陈琳', 2017211671, '男', '17398389577', '993561009', 7, 1504438301, 1, 0, 1, '未知', '未知', '我是电气工程及其自动化六班的陈琳。本人幽默风趣，容易相处，做事细致认真，责任感强，且爱好广泛，爱好阅读，手工作品。会简易使用pr做视频和压制软件。我希望能够加入视频部来学习更多有关做视频的知识，积累工作经验，结交朋友，丰富大学生活。我将以热情积极的态度学习技能，投入工作。希望能与明理苑共同发展。');
INSERT INTO `oa_recruit` VALUES (48, '滕娴', 2017211679, '男', '17356585412', '1195491991', 8, 1504442690, 2, 0, 1, '未知', '未知', '会一些简单的办公软件，会写一些简单的策划，不过都不是很专业和深入，希望能向学长学姐们学习一些策划的经验。然后性格比较开朗，能跟其他人好好沟通。 希望能加入明理苑策划部');
INSERT INTO `oa_recruit` VALUES (49, '郝宁', 2015210839, '女', '18756054850', '1046137954', 9, 1504448486, 2, 0, 1, '机械与汽车工程学院', '能源与动力工程', '喜欢写作的姑娘一枚，大一大二在机汽学院宣传外联部工作，有运营院学生会微信及QQ的经验。非常希望可以加入到明理苑这个大家庭中来！');
INSERT INTO `oa_recruit` VALUES (50, '乔晓娜', 2016214290, '女', '15656050941', '2055658234', 1, 1504503832, 2, 0, 1, '管理学院', '会计学', '你们好！\n我是乔晓娜，2016届管理学院会计专业学生，待人待事耐心，性格温和。\n明理苑的目标是做有意义的产品，立足新媒体运营做互联网产品。办公室的责任是致力于与外界做好沟通，保障日常工作正常进行，并且组织、协调各部门工作。\n之所以申请明理苑办公室的职位，有三点个人优点：\n一：我做事认真负责，不会耽误工作，并且能够融洽的融于集体中，适合办公室的安排、组织、协调工作。\n二：大一一年无挂科补考科目，能妥善安排好个人工作和学习时间。\n三：大二只申请了明理苑一个校园职位，学习之余有充分的时间做好本职工作。并且本人掌握一些微信排版的技巧，本职工作之余，可以协助其他部门工作。\n如果很荣幸申请成功，我将认真耐心对待工作，努力学习还不熟练的地方，热情对待合作的同学，积极创建和维护组织中的“家文化”。谢谢！');
INSERT INTO `oa_recruit` VALUES (51, '焦晓婷', 2015214077, '女', '18856045351', '1712013959', 1, 1504509553, 1, 4, 2, '数学学院', '数学与应用数学', '中共预备党员；\n本学期成绩排名专业12左右，已过英语四六级；\n性格开朗活泼、喜欢与人交流，做事情又有数学人的严谨；\n曾任数学学院心视界中心主任、联想idea精英汇素拓部部长；\n曾获优秀学生干部称号、新生杯辩论赛最佳辩手；\n组织策划、管理协调能力较好；\n组织过校级活动手语合唱比赛；\n对OFFICE较为熟悉，ps可以掌握简单的操作；\n多次参与志愿服务活动，例如献血、“善行一百”等；');
INSERT INTO `oa_recruit` VALUES (52, '张哲', 2015210859, '男', '18756913992', '934821072', 2, 1504525132, 1, 1, 1, '机械与汽车工程学院', '能源与动力工程', '一名大三学生，曾任汽交学院生活部部长一职，获学院优秀学生干部殊荣；而来到老区又是新的开始，愿重新开始，锻炼自己，学到更多，真诚的希望加入明理苑工作室，为全校师生做出一些贡献。');
INSERT INTO `oa_recruit` VALUES (53, '刘克江', 2016212421, '男', '13215518510', '1253725427', 3, 1504529334, 1, 4, 2, '化学与化工学院', '化学工程与工艺', '因为兴趣爱好，我选择了这个部门，有一点编程的基础，没有太多的天赋，却也不是太笨:凭着自己一腔热情，自学拿了C语言二级证书，虽然这个并不能证明什么，至少可以靠努力实现自己想做的事情——而下一个目标就是加入明理苑技术部。');
INSERT INTO `oa_recruit` VALUES (54, '常立', 2017213055, '男', '17318588122', '1334084044', 8, 1504540768, 1, 0, 1, '未知', '未知', '像向日葵一样的热爱');
