INSERT INTO `oa_recruit` VALUES (62, '王朗', 2016211752, '女', '15357882907', '1144652872', 4, 1504762823, 1, 4, 1, '电气与自动化工程学院', '智能电网信息工程', '我学过三年国画，一年素描，也一直有一个平面设计的梦，希望能在明理苑向各位大神们学习，在服务部门的同时提升自己。');
INSERT INTO `oa_recruit` VALUES (63, '彭二丹', 2016214472, '女', '18388923247', '1416600214', 8, 1504811544, 1, 1, 1, '管理学院', '电子商务', '我是管理学院电商16级学生，性格活泼外向（只对熟人）比较耿直，喜欢有刺激性的事情，偏爱看恐怖电影。大一的时候曾在学生会的综合服务部任职，在综服的时候学到了不少，也策划了不少的活动，其实策划还是很头脑风暴的，我知道我在这方面还远远不够，所以我想继续学习策划，继续头脑风暴。');
