INSERT INTO `oa_recruit` VALUES (105, '徐浩翔', 2017212142, '男', '17318582170', '1125239717', 3, 1505041277, 2, 0, 2, '计算机与信息学院', '电子信息工程', '我是工大2017级的新生，来自山东淄博的徐浩翔。因为对技术的热爱和追求，我从一开始便坚定不移地选择了计算机与信息学院的电子信息工程。也许我现在对电脑技术还一无所知，但我相信凭着我不懈的努力和追求我必将成为新时代的技术人才。');
