INSERT INTO `oa_recruit` VALUES (129, '赵奕凯', 2017214100, '男', '13675651337', '1836429574', 4, 1505135374, 1, 0, 1, '管理学院', '工商管理', '17级工管学弟 初高中都当过学习委员 初中兼任副班长 学习过编程 会用ps');
INSERT INTO `oa_recruit` VALUES (130, '朱赟', 2016214404, '男', '17354150598', '914783037', 2, 1505143344, 1, 3, 1, '管理学院', '电子商务', '我是电商1班的，我性格开朗活泼，爱好篮球、王者荣耀等，为人幽默，很擅长与人交往，关系网还行，我相信我能够胜任这个工作');
INSERT INTO `oa_recruit` VALUES (131, '刘珍珍', 2017210404, '女', '17356582403', '3695143943', 4, 1505145733, 1, 0, 2, '机械工程学院', '机械设计制造及其自动化', '大家好，我是机设二班的刘珍珍。我来自甘肃会宁。我平时喜欢乒乓球，羽毛球，台球，阅读，跑步，玩游戏，漫画，泡剧......我生性乐观开朗，喜欢交朋友，更喜欢安静的设计，各个领域，我都很热衷。总之，我爱学习，但此学习非彼学习哦。');
INSERT INTO `oa_recruit` VALUES (132, '权昭瑄', 2015213497, '男', '15605652363', '393197860', 5, 1505170866, 2, 0, 1, '管理学院', '信息管理与信息系统', '我非常想加入产品部，做有关原型的产品，我学软件很快的');
