INSERT INTO `oa_recruit` VALUES (191, '王星颖', 2016214435, '女', '17356511375', '2845308721', 9, 1505276329, 1, 4, 1, '管理学院', '电子商务', '我是一名大二学生，不会ps，不懂很多技术的应用，但很渴望加入这个部门，因为我有一颗认真负责并且求知欲望很强的心，我想习，学会怎样运营微信微博，我相信我可以做到。');
