INSERT INTO `oa_recruit` VALUES (193, '毛召锋', 2014212634, '男', '15556960952', '870602216', 5, 1505279667, 2, 0, 1, '土水学院', '测绘14-1班', '个人有过大型企事业单位和互联网公司产品部实习经历，虽然做的都是基础岗位，但是锻炼了自己，在即将毕业之际，为了不留遗憾，在兴趣驱动下，希望加入你们，和你们一起成长！');
INSERT INTO `oa_recruit` VALUES (194, '王伟', 2017212011, '男', '18756937922', '2415855166', 3, 1505280165, 2, 2, 2, '计算机与信息学院', '计算机科学与技术', '我是计算机科学与技术新生，虽然现在我在专业上无法给技术部太大帮助，但我觉的一年后我是可以的，而且这一年我也可以去其它部门帮帮忙呀。');
INSERT INTO `oa_recruit` VALUES (195, '魏金兰', 2015212047, '女', '18756922922', '378038265', 1, 1505280321, 2, 1, 1, '化学与化工学院', '高分子材料与工程', '性格开朗，热情乐观\n在班级担任班级团支书，有较强的组织能力，统筹兼顾能力，组织的团日活动获校级一等奖，班级也获得先进团支部，先进班级。\n在两年暑假，参加为期一个月的社会支教活动（担任队长），三下乡，社会水调研。');
INSERT INTO `oa_recruit` VALUES (196, '黄晶丽', 2015213503, '女', '17768366769', '1541052470', 9, 1505281738, 1, 4, 1, '管理学院', '信息管理与信息系统', '我是管理学院信息管理与信息系统15-2班黄晶丽，曾在青志联环保部门服务，参加过社团工作和大型志愿者活动，对微信微博公众号的运营方式与文稿编辑有浓厚的兴趣和基本的了解，具有一定的能力，希望可以加入行列中共同努力发展。');
INSERT INTO `oa_recruit` VALUES (197, '马银婕', 2015213495, '女', '15556908680', '402711537', 9, 1505281841, 1, 4, 1, '管理学院', '信息管理与信息系统', '本人是管理学院的学生，对微信微博运营很有兴趣，也比较关注热点问题。曾在兴趣社团的宣传部工作，负责QQ的运营，但是比较浅显。希望加入微信微博部，增长才干，认真学习。');
INSERT INTO `oa_recruit` VALUES (198, '戴柯寒', 2017111092, '男', '15755167644', '2557732213', 3, 1505286539, 2, 0, 1, '未知', '未知', '本科毕业于合肥工业大学微电子科学与工程专业，校三等奖学金，参加了瑞撒车模部以及新风社，帮助过孤寡老人，大二成为瑞撒部长，参加全国大学生数学建模大赛获得省二等奖，考研准备东南大学，复试被刷调剂回工大，对软件方向比较感兴趣，但是调剂回来没有如愿，希望加入明理苑拓宽视野，个人爱好非常喜欢打篮球，书法，下棋，目前在学吉他。');
INSERT INTO `oa_recruit` VALUES (199, '黄荣超', 2017210300, '男', '17805659501', '1691419256', 8, 1505289545, 1, 1, 2, '仪器科学与光电工程学院', '生物医学工程', '我叫黄荣超，自安徽芜湖，我从小就有着很多稀奇古怪的想法。小学的时候，看到大些的小朋友在玩掌机游戏，就自己用笔纸做出游戏和小伙伴玩，比如口袋妖怪，自己写技能，写参数，裁剪小纸片并在上面写精灵的名字和技能，用计算器的随机数来判断技能是否命中等。以及创作规则独特的游戏，例如真人三国杀。\n   不过这些想法由于平台的缘故在长大了之后变的十分幼稚，所以我希望我可以有一个更好的平台，让我的想象力可以被充分的发挥出来');
INSERT INTO `oa_recruit` VALUES (200, '程宇', 2015213375, '男', '13225727695', '814202006', 1, 1505299252, 2, 0, 2, '电子科学与应用物理学院', '微电子科学与工程', '我叫程宇，今年大三，虽然有点老，但我还是非常想加入明理苑');
INSERT INTO `oa_recruit` VALUES (201, '龚愉琴', 2017214211, '女', '17856530679', '376908079', 9, 1505306932, 2, 0, 1, '管理学院', '电子商务', '我叫龚愉琴，来自管理学院电子商务2班。性格活泼开朗，待人热情大方，自认为做事比较严谨认真。曾在初、高中连任班长，锻炼了自己的管理能力，培养了自己的责任心。进入大学想加入一个优秀的组织锻炼自己，丰富自己的课余生活，提高自己团结协作的能力。微信微博作为现代普遍使用的社交软件，提供平台给大众看这个世界。我平日也经常使用这些软件，希望能加入微信微博部学会管理公众号等的运营，提高自己的计算机使用能力，也期待与更多优秀的学长学姐一起共事！');
INSERT INTO `oa_recruit` VALUES (202, '任婕', 2017211736, '女', '18035206835', '13593026835', 9, 1505310616, 2, 0, 1, '电气与自动化工程学院', '智能电网信息工程', '对新鲜资讯感兴趣且富有热情，非常希望得到一个机会锻炼自己，提升自己。');
INSERT INTO `oa_recruit` VALUES (203, '乔晓娜', 2016214290, '女', '15656050941', '2055658234', 9, 1505311365, 1, 4, 1, '管理学院', '会计学', '你们好！\n我是2016级管理学院会计一班学生乔晓娜。性格温和，待人待事有耐心。之所以申请微信微博部的岗位，我有三点优势：\n一：大二我只报名参选明理苑大学生网络文化工作室微信微博部一个岗位，所以有充分的时间完成指定任务；\n二：大一期间曾参加两个部门，在认真及时完成工作的情况下没有挂科补考科目，可以合理安排自己的时间；\n三：大一期间掌握了一些微信排版技术，可以独立完成一篇稿子的排版，且尝试并在团委公众号上发过文稿，对于新工作容易上手，有一定的能力。');
INSERT INTO `oa_recruit` VALUES (204, '姜琦', 2016215052, '女', '15256056958', '1379877484', 2, 1505316327, 1, 4, 2, '外国语学院', '英语（国际贸易）', '大二，外国语学院。性格开朗，喜欢结交朋友。想象力丰富，希望自己能够尽力做好一份工作。现担任青志联副队长。');
