INSERT INTO `oa_recruit` VALUES (206, '李阳', 2016212707, '男', '17305699674', '1098130820', 9, 1505384163, 2, 0, 1, '土木与水利工程学院', '土木工程', '提笔不为风雅，只说这花开荼蘼。虽说我只是一个在别人光环下黯然失色的小角色，可是穷且益坚，不坠青云之志。我总想搞些事，让我有事可做，愿有花有草，有梦想。很多人输于岁月，匆匆忙忙，蹉跎而已，我想用文字支持每个人应该走的路，应该去做的事。愿我走出半生，归来仍是少年。');
